// frontend/src/components/ProfilePage.vue

<template>
  <div>
    <h2>Profil de l'utilisateur</h2>
    <form @submit.prevent="updateUser">
      <label for="nom">Nom:</label>
      <input type="text" id="nom" v-model="user.nom"><br>
      <label for="email">Email:</label>
      <input type="email" id="email" v-model="user.email"><br>
      <label for="age">Âge:</label>
      <input type="number" id="age" v-model="user.age"><br>
      <button type="submit">Mettre à jour</button>
      <button @click.prevent="deleteUser">Supprimer</button>
    </form>
  </div>
</template>

<script>
import { getUser, updateUser, deleteUser } from '@/services/ProfilPage';

export default {
  data() {
    return {
      user: {
        id: 3, // Utilisateur avec l'id 3 pour l'instant
        nom: '',
        email: '',
        age: ''
      }
    };
  },
  mounted() {
    this.fetchUserData();
  },
  methods: {
    async fetchUserData() {
      try {
        this.user = await getUser(this.user.id);
      } catch (error) {
        // Gérer l'erreur
      }
    },
    async updateUser() {
      try {
        await updateUser(this.user.id, this.user);
      } catch (error) {
        // Gérer l'erreur
      }
    },
    async deleteUser() {
      try {
        await deleteUser(this.user.id);
        // Rediriger ou faire d'autres actions après suppression de l'utilisateur
      } catch (error) {
        // Gérer l'erreur
      }
    }
  }
};
</script>
